import React, { Component } from "react";
import { WriterHeader, WriterSwitch, WriterItem,WriterInfo ,WriterName, WriterDesc, WriterFocus, AllWriter } from "../style";
class Writer extends Component {
    render() {
        return (
            <div>
                <WriterHeader>推荐作者
                    <WriterSwitch>换一批</WriterSwitch>
                </WriterHeader>
                <WriterItem>
                    <img className="head" src="https://ss2.bdstatic.com/70cFvnSh_Q1YnxGkpoWK1HF6hhy/it/u=1074417518,1198280004&fm=26&gp=0.jpg" 
                    alt="" />
                    <WriterInfo>
                        <WriterName>梅拾璎</WriterName>
                        <WriterDesc>写了151.5k字 · 13k喜欢</WriterDesc>
                        <WriterFocus>+关注</WriterFocus>
                    </WriterInfo>
                </WriterItem>
                <AllWriter>查看全部</AllWriter>
            </div>

        )
    }
}

export default Writer
