import * as actionTypes from "./actionTypes"
import axios from "axios";
export const Focus = () => ({
    type:actionTypes.FOCUS
})

export const Blur = () => ({
    type:actionTypes.BLUR
})

export const getList = () => {
    return (dispatch) => {
        axios.get('/api/headerList.json')
    }
}
