import React, { Component } from "react";
import { CSSTransition } from 'react-transition-group';
import {
    HeadWrapper, Logo, Nav, NavItem, Navsearch, Addition,
    Button, SearchWrapper
} from './style'
class Header extends Component {

    constructor(props){
        super(props);
        this.state = {
            focused:true
        }
        this.handleInputFocus = this.handleInputFocus.bind(this)
        this.handleInputBlur = this.handleInputBlur.bind(this)
    }
    render() {
        return (
            <HeadWrapper>
                <Logo />
                <Nav>
                    <NavItem className='left active'>首页</NavItem>
                    <NavItem className='left'>下载App</NavItem>
                    <NavItem className='right'>登陆</NavItem>
                    <NavItem className='right'>
                        <span className="iconfont">&#xe636;</span>
                    </NavItem>
                    <SearchWrapper>
                    <CSSTransition 
                            in={this.state.focused}
                            timeout={200}
                            className="slide"
                        >
                            <Navsearch
                                className = {this.state.focused ? 'focused' : ''}
                                onFocus={this.handleInputFocus}
                                onBlur={this.handleInputBlur}
                            >
                            </Navsearch>
                        </CSSTransition>
                        <span 
                        className = {this.state.focused ? 'focused iconfont' : 'iconfont'}
                        >&#xe614;</span>
                    </SearchWrapper>
                </Nav>
                <Addition>
                    <Button className='write'>
                        <span className="iconfont">&#xe615;</span>
                        写文章
                    </Button>
                    <Button className="reg">注册</Button>
                </Addition>
            </HeadWrapper>
        )
    }
    handleInputFocus() {
        this.setState(()=>({
            focused: true  // 调用函数handleInputFocus时，改变focused参数
        }))
    }

    handleInputBlur() {
        this.setState(()=>({
            focused: false  // 调用函数handleInputBlur时，改变focused参数
        }))
    }
}
export default Header;
